    } else {
        imagen = <img src='https://raw.githubusercontent.com/marielexoteria/AppNoticias/master/src/img/fallback_no_image.png' alt={title} />
    }

    return ( 
        <div className="col-md-6 col-12 mb-4">

        </div>
     );
}

