import Link from 'next/link';

const Navegacion = () => ( //este arrow function es lo mismo que usar {} y return ();
    <div>
        <ul>
            <h1>Todo Bitcoin</h1>
            <li><Link href="/"><a>Inicio</a></Link></li>
            <li><Link href="/nosotros"><a>Nosotros</a></Link></li>
        </ul>
        <style jsx> {` 
            h1 {
                color: #fff;
            }

            ul {
                background-color: #333;
                list-style: none;
                display: flex;
            }

            ul li {
                padding: 0.5rem 0;
                margin-right: 1rem;
            }

            ul li a {
                font-size: 1.2rem;
                color: #fff;
                text-decoration: none;
            }
        `}
        </style>
    </div>
); //en esta forma de escribir estilos Next.js los aplica solamente a los elementos de este componente (o sea, no se aplican a otros ul en otros archivos)

export default Navegacion;
