const Evento = (props) => {
    //haciendo destructuring para extraer la info que se quiere desplegar
    const {name, url, description} = props.info;

    //para que solamente se desplieguen 300 caracteres de la descripción del evento, si la hubiere
    let desc = description.text;
    if (desc) {
        desc = desc.substr(0,300);
    }

    return (
        <a href={url} target="_blank" className="list-group-item active text-light mb-1">
            <h3 className="mb-3">{name.text}</h3>
            <p className="mb-1">{description.text}</p>
        </a>
      );
}

export default Evento;
